import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'movies-item',
  templateUrl: './movies-item.component.html',
  styleUrls: ['./movies-item.component.css']
})
export class MoviesItemComponent implements OnInit {

  @Input() image?: string;
  @Input() title?: string;
  @Input() id?: number;

  ngOnInit() {
    }
}
<div class="card" style="width: 18rem;">
  <movies-item-image [image]="image"></movies-item-image>
  <div class="card-body">
    <p class="card-text" style="width: 100%; text-align: center;">
      <movies-item-title [title]="title" [id]="id"></movies-item-title>
    </p>

  </div>
</div>
