import { Component, OnInit } from '@angular/core';
import { DataService } from "../../services/data.service";
import { Router } from "@angular/router";
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'edit-movie',
  templateUrl: './edit-movie.component.html',
  styleUrls: ['./edit-movie.component.css']
})
export class EditMovieComponent implements OnInit {

   public image = '';
   public title?: string = '';
   public year?: string;
   public description?: string;
   public id?: string | undefined;

   public newEditedMovie = {
       id: '',
       title: '',
       year: '',
       image: '',
       description: ''
     };

     public logged?: boolean;
     public logout?: boolean;

     constructor(private dataService: DataService, private route: ActivatedRoute, private router: Router) {
     }


     ngOnInit(): void {
        let id: string = '';
        this.route.paramMap.subscribe((params: any) => {
            id = params.get('id');
        });
        this.dataService.getById(id).subscribe((res: any) => {
            this.image = res['image'];
            this.title = res['title'];
            this.year = res['year'];
            this.description = res['description'];
            this.newEditedMovie.title = res['title'];
            this.newEditedMovie.year = res['year'];
            this.newEditedMovie.image = res['image'];
            this.newEditedMovie.description = res['description'];
        });
     }

     editMovie() {
        this.dataService.createOrUpdate(this.newEditedMovie).subscribe((result) => {
          this.router.navigate(['/'])
        })
     }
}
<div class="container">
  <h1 class="entry-title"><span>Edytuj film</span></h1><hr>
  <form action="post" autocomplete="off" method="POST">
    <div class="input-group">
      <input [(ngModel)]="newEditedMovie.title" type="text" class="form-control"
             name="title" placeholder="Tytuł" title="Tytuł">
    </div>

    <div class="input-group">
      <input [(ngModel)]="newEditedMovie.year" type="text" class="form-control"
             name="title" placeholder="Rok" title="Rok">
    </div>

    <div class="input-group">
      <input [(ngModel)]="newEditedMovie.image" type="text" class="form-control"
             name="title" placeholder="URL plakatu" title="Image">
    </div>

    <div class="input-group">
      <input [(ngModel)]="newEditedMovie.description" type="text" class="form-control"
             name="title" placeholder="Opis" title="Opis">
    </div>

    <button (click)="editMovie()" class="btn btn-primary" type="submit" title="Wprowadź zmiany">
      Wprowadź zmiany
    </button>
  </form>
</div>
